package uz.coder.realtimedatabase.modelsimport java.io.Serializableclass User : Serializable {    var displayName: String? = null    var userName: String? = null    var photoUrl: String? = null    var id: Int? = null    var password: String? = null    var online: Boolean? = null    constructor(        displayName: String?,        userName: String?,        photoUrl: String?,        id: Int?,        password: String?,        online: Boolean?    ) {        this.displayName = displayName        this.userName = userName        this.photoUrl = photoUrl        this.id = id        this.password = password        this.online = online    }    constructor()    override fun toString(): String {        return "User(displayName=$displayName, userName=$userName, photoUrl=$photoUrl, id=$id, password=$password, online=$online)"    }}